// index.js (backend)
// Antes de definir las rutas, definimos una lista de usuarios y contraseñas
const users = [
  { username: 'usuario1', password: 'contraseña1' },
  { username: 'usuario2', password: 'contraseña2' },
  { username: 'usuario3', password: 'contraseña3' }
];

// Luego, definimos la ruta de autenticación de inicio de sesión
app.post("/login", (req, res) => {
  const { username, password } = req.body;

  // Verificamos si el usuario y la contraseña coinciden con algún usuario en la lista
  const user = users.find(user => user.username === username && user.password === password);

  // Si encontramos un usuario que coincida, devolvemos un estado 200 (OK)
  // De lo contrario, devolvemos un estado 401 (Unauthorized)
  if (user) {
      res.status(200).send("Login successful");
  } else {
      res.status(401).send("Invalid credentials");
  }
});

// Login.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './styles.css';

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:3006/login', { username, password });
      if (response.status === 200) {
        navigate('/app');
      }
    } catch (error) {
      setError('Credenciales inválidas');
    }
  };

  return (
    <div className="main-container">
      <div className="container d-flex justify-content-center align-items-center" style={{ height: '80vh' }}>
        <div className="card text-center">
          <div className="card-header">
            <h2>Mr. Frog</h2>
          </div>
          <div className="card-body">
            <input
              type="text"
              className="form-control mb-3"
              placeholder="Usuario"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              className="form-control mb-3"
              placeholder="Contraseña"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            {error && <p className="text-danger">{error}</p>}
            <button className="btn btn-primary" onClick={handleLogin}>
              Iniciar sesión
            </button>
          </div>
        </div>
      </div>
      <footer className="footer">
        <p>© Mr.Frog 2024 . Todos los derechos reservados.</p>
      </footer>
    </div>
  );
}

export default Login;
