import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './styles.css'; 

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:3006/login', {
        username,
        password,
      });
      console.log(response.data); // Aquí podrías manejar la respuesta del servidor según sea necesario
      navigate('/app'); // Redirige al usuario a la página de la aplicación si la autenticación es exitosa
    } catch (error) {
      setError('Credenciales inválidas'); // Mensaje de error genérico
      console.error('Error al iniciar sesión:', error);
    }
  };

  return (
    <div className="main-container">
      <div className="container d-flex justify-content-center align-items-center" style={{ height: '80vh' }}>
        <div className="card text-center">
          <div className="card-header">
            <h2>Inicio de Sesión</h2>
          </div>
          <div className="card-body">
            <input type="text" className="form-control mb-3" placeholder="Usuario" value={username} onChange={(e) => setUsername(e.target.value)} />
            <input type="password" className="form-control mb-3" placeholder="Contraseña" value={password} onChange={(e) => setPassword(e.target.value)} />
            {error && <p className="text-danger">{error}</p>} {/* Muestra el mensaje de error si existe */}
            <button className="btn btn-primary" onClick={handleLogin}>Iniciar sesión</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
